mapper.select:
select b.id as structureId,b.title,a.id as classifyId,a.exam as
exam,a.jlpt_level as jlptLevel,a.jtest_level as jtestLevel from
tb_question_classify a right join tb_question_structure b on
a.id=b.classify_id
where a.exam = #{exam}

	and a.jlpt_level=#{jlptLevel}


	and a.jtest_level=#{jtestLevel}

order by structureId

        select * from vb_question_detail where no not in(select question_id from tb_exam_collection
        where user_id = #{userId}
        ) and structureId = #{structureId} and deleteFlag='0'
		
		and source like #{year} 
		
		order by no

select z.subject, c.no, c.context, c.context_key as contextKey, c.context_after as contextAfter, c.a, c.b, c.c, c.d, c.answer, c.analysis, c.source, c.father_id as fatherId, c.classify_id as classifyId, c.structure_id as structureId from (select a.id, a.subject, a.delete_flag as deleteFlag from tb_first_level_directory a, (select q.father_id as fatherId from tb_question_classify d , (select * from tb_question where no not in (select question_id from tb_collection where id=#{userId}) ) q where q.classify_id=d.id and q.delete_flag = '0'
and exam = #{exam,jdbcType=CHAR}
and jlpt_level = #{jlptLevel,jdbcType=CHAR}
and jtest_level = #{jtestLevel,jdbcType=CHAR}
and exam_type = #{examType,jdbcType=CHAR}
order by q.no limit 1) w where id = w.fatherId and a.delete_flag=0) z right join tb_question c on z.id = c.father_id where c.father_id =z.id

select `q`.`no` AS `no`,`q`.`context` AS `context`,`q`.`context_key` AS `contextKey`,`q`.`context_after` AS `contextAfter`,`q`.`a` AS `a`,`q`.`b` AS `b`,`q`.`c` AS `c`,`q`.`d` AS `d`,`q`.`answer` AS `answer`,`q`.`analysis` AS `analysis`,`q`.`delete_flag` AS `deleteFlag`,`q`.`source` AS `source`, q.classify_id AS classifyId, q.structure_id AS structureId, q.father_id as fatherId, `d`.`exam` AS `exam`,`d`.`exam_type` AS `examType`,`d`.`jlpt_level` AS `jlptLevel`,`d`.`jtest_level` AS `jtestLevel` from tb_question_classify d, (select * from tb_question where no not in (select question_id from tb_collection where id=#{userId}) ) q where q.classify_id=d.id and q.delete_flag = '0'
and exam = #{exam,jdbcType=CHAR}
and jlpt_level = #{jlptLevel,jdbcType=CHAR}
and jtest_level = #{jtestLevel,jdbcType=CHAR}
and exam_type = #{examType,jdbcType=CHAR}
limit 5

select id from tb_question_classify where delete_flag = 0

	and exam = #{exam,jdbcType=CHAR}


	and jlpt_level = #{jlptLevel,jdbcType=CHAR}


	and jtest_level = #{jtestLevel,jdbcType=CHAR}


	and exam_type = #{examType,jdbcType=CHAR}

order by id
limit 1
